# 사무판단
law_raw &lt;- read_excel("data/(230410)최종결과표(통합본).xlsx", skip = 0)

law_tbl &lt;- law_raw |&gt;
  janitor::clean_names(ascii = FALSE) |&gt; 
  mutate(사무_판단 = ifelse(is.na(사무_판단), 0, 사무_판단)) |&gt; 
  mutate(사무유형 = str_remove(사무유형, "\r\n")) |&gt;
  # 사무유형: 국가, 시도, 공동, ...
  mutate(사무유형 = case_when(사무유형 == "국가시도" ~ "국가-시도",
                              사무유형 == "정부" ~ "국가",
                              사무유형 == "공동" ~ "국가-시도",
                              is.na(사무유형) ~ "없음",
