Venn diagram with ggVennDiagram


코드
library(ggVennDiagram)


affairs_type &lt;- law_tbl |&gt;
  filter(사무유형 != "없음") |&gt;
  group_by(법령명) |&gt;
  count(사무유형) |&gt;
  arrange(법령명, 사무유형) |&gt;
  ungroup() |&gt;
  separate(사무유형, into = c("사무1", "사무2", "사무3"), sep = "-") |&gt;
  mutate(번호 = row_number()) |&gt; 
  mutate(사무1 = str_glue("{사무1}_{번호}"),
         사무2 = str_glue("{사무2}_{번호}"),
         사무3 = str_glue("{사무3}_{번호}")) |&gt; 
  select(-법령명, -n, -번호) |&gt; 
  pivot_longer(사무1:사무3, names_to = "사무구분", values_to = "사무") |&gt; 
  filter(!str_detect(사무, "^NA")) |&gt; 
  mutate(집합 = case_when(str_detect(사무, "국가") ~ "국가",
                          str_detect(사무, "시도") ~ "시도",
                          str_detect(사무, "시군구") ~ "시군구",
                          TRUE ~ "기타")) |&gt; 
  mutate(원소 = str_extract(사무, "\\d{1,5}"))


korea_type &lt;- affairs_type |&gt; 
  filter(집합 == "국가") |&gt; 
  pull(원소) |&gt; 
  unlist()

sido_type &lt;- affairs_type |&gt; 
  filter(집합 == "시도") |&gt; 
  pull(원소) |&gt; 
  unlist()

sgg_type &lt;- affairs_type |&gt; 
  filter(집합 == "시군구") |&gt; 
  pull(원소) |&gt; 
  unlist()

ggVennDiagram(list(국가 = korea_type, 시도 = sido_type, 시군구 = sgg_type),
              category.names = c("국가",
                                 "시도",
                                 "구시군"),
               label_alpha = 0,
               color = 1, lwd = 0.7) +
  scale_fill_gradient(low = "#F4FAFE", high = "#4981BF") +
  theme(legend.position = "none")

코드
library(tidyverse)
library(readxl)
library(gt)
library(gtExtras)


# 사무판단
law_raw &lt;- read_excel("data/(230410)최종결과표(통합본).xlsx", skip = 0)

law_tbl &lt;- law_raw |&gt;
  janitor::clean_names(ascii = FALSE) |&gt; 
  mutate(사무_판단 = ifelse(is.na(사무_판단), 0, 사무_판단)) |&gt; 
  mutate(사무유형 = str_remove(사무유형, "\r\n")) |&gt;
  # 사무유형: 국가, 시도, 공동, ...
  mutate(사무유형 = case_when(사무유형 == "국가시도" ~ "국가-시도",
                              사무유형 == "정부" ~ "국가",
                              사무유형 == "공동" ~ "국가-시도",
                              is.na(사무유형) ~ "없음",
                              TRUE ~ 사무유형))  |&gt; 
  rename(부처명 = 소관_부처명)

## 법률, 시행령, 시행규칙
## 법령_구분 : 데이터 전처리 (정제작업, 오분류, 결측값)
law_set &lt;- read_excel("data/법령정보.xlsx", sheet = '법령목록') |&gt; 
  janitor::clean_names(ascii = FALSE) |&gt; 
  rename(법시시 = 법률_시행령_시행규칙_기타구분)


law_type &lt;- law_tbl |&gt; 
  group_by(법령명, 법령_구분) |&gt;
  summarise(조항수 = n()) |&gt;
  ungroup() |&gt; 
  mutate(법령_구분 = as.character(법령_구분)) |&gt; 
  fill(법령_구분, .direction = "down") |&gt;
  mutate(법령구분 = case_when(법령_구분 == "1" ~ "법률",
                              법령_구분 == "2" ~ "시행령",
                              법령_구분 == "3" ~ "시행규칙",
                              TRUE ~ 법령_구분)) |&gt; 
  left_join(law_set |&gt; select(법령명, 법시시) |&gt; filter(법시시 != "NA"), by = "법령명") |&gt;
  mutate(법시시 = if_else(is.na(법시시) | 법시시 == "기타", 법령구분, 법시시)) |&gt;
  mutate(법령구분 = if_else(법령구분 == "법시시", 법령구분, 법시시)) |&gt; 
  select(법령명, 법령구분 = 법시시)

law_type |&gt; 
  count(법령구분, name = "법령수")
