코드
library(tidyverse)
library(readxl)
library(gt)

law_raw &lt;- read_excel("data/(230410)최종결과표(통합본).xlsx", skip = 0)

law_tbl &lt;- law_raw |&gt;
  janitor::clean_names(ascii = FALSE) |&gt; 
  mutate(사무_판단 = ifelse(is.na(사무_판단), 0, 사무_판단)) |&gt; 
  mutate(사무유형 = str_remove(사무유형, "\r\n")) |&gt;
  # 사무유형: 국가, 시도, 공동, ...
  mutate(사무유형 = case_when(사무유형 == "국가시도" ~ "국가-시도",
                              사무유형 == "정부" ~ "국가",
                              사무유형 == "공동" ~ "국가-시도",
                              is.na(사무유형) ~ "없음",
                              TRUE ~ 사무유형))  |&gt; 
  rename(부처명 = 소관_부처명)

2.2 법령별 사무판단 통계


코드
law_tbl |&gt;
  group_by(법령명) |&gt; 
  summarise(사무판단수 = sum(사무_판단)) |&gt; 
  mutate(법령사무판단 = if_else(사무판단수 &gt; 0, "사무판단", "사무판단없음")) |&gt; 
  count(법령사무판단, name = "법령수") |&gt; 
  mutate(비율 = 법령수 / sum(법령수)) |&gt; 
  gt() |&gt; 
  cols_align('center') |&gt; 
  fmt_integer(법령수) |&gt; 
  fmt_percent(비율, decimals = 1) |&gt; 
  grand_summary_rows(columns = c(법령수, 비율), fns = list(합계 = ~sum(.)),
                     fmt = ~ fmt_number(., sep_mark = ',', decimals =0, use_seps = FALSE)) |&gt; 
  tab_header(title = "법령별 사무판단 여부", 
             subtitle = "사무판단 있는 법령: '사무판단', 없는 법령: '사무판단' 표시없음") |&gt;
  gtExtras::gt_theme_538()








